                "<!DOCTYPE html>\n<html lang=\"es\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Nueva Actividad (quiz)</title>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            margin: 0;\n            padding: 0;\n            background-color: #f4f4f4;\n            display: flex;\n            justify-content: center;\n            align-items: center;\n            min-height: 100vh;\n        }\n\n        .quiz-container {\n            background-color: #fff;\n            border-radius: 8px;\n            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);\n            width: 80%;\n            max-width: 600px;\n            padding: 20px;\n            margin: 20px;\n            text-align: center;\n        }\n\n        h1 {\n            color: #333;\n        }\n\n        .question {\n            margin-bottom: 20px;\n        }\n\n        .options {\n            display: flex;\n            flex-direction: column;\n            align-items: flex-start;\n        }\n\n        .option {\n            margin-bottom: 10px;\n            padding: 10px;\n            border: 1px solid #ccc;\n            border-radius: 5px;\n            width: 90%;\n            text-align: left;\n            cursor: pointer;\n            background-color: #f9f9f9;\n        }\n\n        .option:hover {\n            background-color: #e9e9e9;\n        }\n\n        .correct {\n            background-color: #d4edda;\n            border-color: #c3e6cb;\n            color: #155724;\n        }\n\n        .incorrect {\n            background-color: #f8d7da;\n            border-color: #f5c6cb;\n            color: #721c24;\n        }\n\n        .progress-bar {\n            height: 10px;\n            background-color: #ddd;\n            border-radius: 5px;\n            margin-bottom: 20px;\n        }\n\n        .progress {\n            height: 10px;\n            background-color: #4CAF50;\n            border-radius: 5px;\n            width: 0%;\n        }\n\n        .result {\n            font-weight: bold;\n            margin-top: 20px;\n        }\n\n        @media (max-width: 600px) {\n            .quiz-container {\n                width: 95%;\n                margin: 10px;\n                padding: 10px;\n            }\n             .option {\n                width: 85%;\n            }\n        }\n    </style>\n</head>\n<body>\n\n    <div class=\"quiz-container\">\n        <h1>Nueva Actividad (quiz)</h1>\n        <p>Quiz diagn√≥stico para medir nivel de conocimiento sobre el diario emocional.</p>\n\n        <div class=\"progress-bar\">\n            <div class=\"progress\" id=\"progress\"></div>\n        </div>\n\n        <div id=\"question-container\" class=\"question\"></div>\n\n        <div id=\"options-container\" class=\"options\"></div>\n\n        <div id=\"result\" class=\"result\" style=\"display: none;\"></div>\n    </div>\n\n    <script>\n        const questions = [\n            {\n                question: \"¬øQu√© es un diario emocional?\",\n                options: [\n                    { text: \"Un registro de eventos diarios sin enfoque en emociones.\", correct: false },\n                    { text: \"Un cuaderno para expresar y explorar sentimientos y emociones.\", correct: true },\n                    { text: \"Una lista de tareas pendientes.\", correct: false }\n                ]\n            },\n            {\n                question: \"¬øCu√°l es uno de los beneficios de llevar un diario emocional?\",\n                options: [\n                    { text: \"Aumentar el estr√©s y la ansiedad.\", correct: false },\n                    { text: \"Mejorar la autoconciencia y la regulaci√≥n emocional.\", correct: true },\n                    { text: \"Olvidar r√°pidamente los problemas.\", correct: false }\n                ]\n            },\n            {\n                question: \"¬øQu√© tipo de contenido es adecuado para un diario emocional?\",\n                options: [\n                    { text: \"Solo eventos positivos.\", correct: false },\n                    { text: \"Solo eventos negativos.\", correct: false },\n                    { text: \"Sentimientos, pensamientos, recuerdos y reflexiones sobre experiencias.\", correct: true }\n                ]\n            },\n            {\n                question: \"¬øCon qu√© frecuencia se recomienda escribir en un diario emocional?\",\n                options: [\n                    { text: \"Solo cuando se sienta extremadamente mal.\", correct: false },\n                    { text: \"Una vez al mes.\", correct: false },\n                    { text: \"Regularmente, seg√∫n las necesidades y preferencias personales.\", correct: true }\n                ]\n            }\n        ];\n\n        let currentQuestionIndex = 0;\n        let score = 0;\n\n        const questionContainer = document.getElementById('question-container');\n        const optionsContainer = document.getElementById('options-container');\n        const resultContainer = document.getElementById('result');\n        const progressBar = document.getElementById('progress');\n\n        function loadQuestion() {\n            const currentQuestion = questions[currentQuestionIndex];\n            questionContainer.textContent = currentQuestion.question;\n\n            optionsContainer.innerHTML = '';\n            currentQuestion.options.forEach((option, index) => {\n                const button = document.createElement('div');\n                button.classList.add('option');\n                button.textContent = option.text;\n                button.addEventListener('click', () => checkAnswer(option.correct, button));\n                optionsContainer.appendChild(button);\n            });\n        }\n\n        function checkAnswer(isCorrect, selectedButton) {\n            const options = document.querySelectorAll('.option');\n            options.forEach(option => {\n                option.disabled = true;\n            });\n\n            if (isCorrect) {\n                score++;\n                selectedButton.classList.add('correct');\n            } else {\n                selectedButton.classList.add('incorrect');\n\n            }\n\n            setTimeout(() => {\n                currentQuestionIndex++;\n                if (currentQuestionIndex < questions.length) {\n                    updateProgressBar();\n                    loadQuestion();\n                    options.forEach(option => {\n                        option.disabled = false;\n                        option.classList.remove('correct', 'incorrect');\n                    });\n                } else {\n                    showResult();\n                }\n            }, 1000);\n        }\n\n        function updateProgressBar() {\n            const progress = (currentQuestionIndex / questions.length) * 100;\n            progressBar.style.width = `${progress}%`;\n        }\n\n\n        function showResult() {\n            questionContainer.style.display = 'none';\n            optionsContainer.style.display = 'none';\n            resultContainer.style.display = 'block';\n\n            let message = \"\";\n            if (score === questions.length) {\n                message = \"¬°Excelente! Demuestras un conocimiento s√≥lido sobre el diario emocional.\";\n            } else if (score >= questions.length / 2) {\n                message = \"¬°Bien! Tienes un buen entendimiento del diario emocional. ¬°Sigue aprendiendo!\";\n            } else {\n                message = \"Es recomendable que explores m√°s sobre el diario emocional. ¬°√Ånimo!\";\n            }\n\n            resultContainer.textContent = `Tu puntaje es: ${score} de ${questions.length}. ${message}`;\n        }\n\n        loadQuestion();\n        updateProgressBar();\n    </script>\n\n</body>\n</html>"
